var board = null;
var game = new Chess();
var $status = null;
var $notation = null;
var $board_holder = null;;
var $sidebar = null;
var $player_indicator = null;
var dialog = null;
var draggingAllowed = true;
var gameHistory = [];
var promotionStart = null;
var promotionTarget = null;
var yourTurn = false;
var moveUploaded = false;
var uploadInterval = null;
var finishInterval = null;
var queryInterval = null;
var finishUploaded = false;
var statusHeight = 0;
var game_turn = null;

var urlParams = new URLSearchParams(window.location.search);
var player = urlParams.get("player");
var boardID = urlParams.get("id");

var white_name = "";
var black_name = "";
var opponent_name = "";

var wP = 'data:image/svg+xml;base64,';
var wN = 'data:image/svg+xml;base64,';
var wB = 'data:image/svg+xml;base64,';
var wR = 'data:image/svg+xml;base64,';
var wQ = 'data:image/svg+xml;base64,';
var wK = 'data:image/svg+xml;base64,';
var bP = 'data:image/svg+xml;base64,';
var bN = 'data:image/svg+xml;base64,';
var bB = 'data:image/svg+xml;base64,';
var bR = 'data:image/svg+xml;base64,';
var bQ = 'data:image/svg+xml;base64,';
var bK = 'data:image/svg+xml;base64,';

function resizeBoard() {
    if(window.innerHeight < window.innerWidth * 0.7) {
        $board_holder.width("99vh");
        $sidebar.height("99vh");
    }
    else {
        $board_holder.width('70vw');
        $sidebar.height('70vw');
    }
    board.resize();

    $notation.height($sidebar.height() - $player_indicator.height() - statusHeight - 7);
}

window.onresize = resizeBoard;

function onDragStart (source, piece, position, orientation) {
    if(!(draggingAllowed && yourTurn)) return false;
    // do not pick up pieces if the game is over
    if (game.game_over()) return false
  
    // only pick up pieces for the side to move
    if ((game.turn() === 'w' && piece.search(/^b/) !== -1) ||
        (game.turn() === 'b' && piece.search(/^w/) !== -1)) {
      return false
    }
}

function onDrop (source, target) {
    // see if the move is legal
    var move = game.move({
      from: source,
      to: target,
      promotion: 'q'
    })
  
    // illegal move
    if (move === null) return 'snapback'
    else if(move.flags.indexOf('p') != -1) {
        game.undo();
        promotionStart = source;
        promotionTarget = target;
        setTimeout(function() {
            dialog.showModal();
        });
        return;
    }
    updateStatus();
    setTimeout(uploadMove());
    uploadInterval = setInterval(uploadMove, 1000);
}

function uploadMove() {
    if(!moveUploaded) {
        var uploadRequest = new Request("https://thegoodlifetravelguru.com/chess/php/make_move.php",
            {method: 'POST', body: 'id=' + boardID + "&player=" + player + "&move=" + encodeURIComponent(gameHistory[gameHistory.length - 1]) + "&fen=" + encodeURIComponent(game.fen()) + "&move_num=" + gameHistory.length,
            headers: {"Content-Type": "application/x-www-form-urlencoded"}});
        fetch(uploadRequest)
            .then(response => response.json()
            .then(data => {
                if(data.result == "success" && !moveUploaded) {
                    moveUploaded = true;
                    clearInterval(uploadInterval);
                }
            }));
    }
}

function notationClicked(element) {
    draggingAllowed = false;
    var moveNum = Number(element.parentNode.children[0].innerText) * 2 - 1;
    if(element.className == "black_move hover") moveNum ++;
    goToHistory(moveNum, element);
}

function goToHistory(moveNum, element) {
    $(".notation_selected").removeClass("notation_selected");
    if(moveNum != game.history().length) {
        game.reset();
        for(var i = 0; i < moveNum; i ++) {
            game.move(gameHistory[i]);
        }
        board.position(game.fen(), true);
    }
    if(moveNum == gameHistory.length) {
        draggingAllowed = true;
    }
    else {
        element.classList.add("notation_selected");
    }
}

function finishGame(result) {
    draggingAllowed = false;
    clearInterval(queryInterval);
    clearInterval(uploadInterval);
    if(game_turn == player) {
        setTimeout(finishGameInterval(result));
        finishInterval = setInterval(finishGameInterval, 1000, result);
    }
}

function finishGameInterval(result) {
    if(!finishUploaded) {
        var finishRequest = new Request("https://thegoodlifetravelguru.com/chess/php/finish_game.php", {method: 'POST', body: 'id=' + boardID + "&result=" + result, headers: {"Content-Type": "application/x-www-form-urlencoded"}});
        fetch(finishRequest)
            .then(response => response.json()
            .then(data => {
                if(data.result == "success" & !finishUploaded) {
                    finishUploaded = true;
                    clearInterval(finishInterval);
                }
            }));
    }
}

function updateStatus () {
    var status = ''

    game_turn = game.turn() == 'w'? "white": "black";
    if(game_turn == player) {
        yourTurn = true;
        status = "Your move";
        if(game.in_checkmate()) {
            status = "Game over, you were checkmated";
        }
    }
    else {
        yourTurn = false;
        status = opponent_name + " to move";
        if(game.in_checkmate()) {
            status = "Game over, you won!";
        }
    }

    if(game.in_checkmate()) {
        finishGame(game_turn == "white"? "black" : "white");
    }
    else if(game.in_check()) {
        status += ", check";
    }
    if(game.in_stalemate()) {
        status = "Game over, draw by stalemate";
        finishGame("stalemate");
    }
    else if(game.in_threefold_repetition()) {
        status = "Game over, draw by repetition"
        finishGame("repetition");
    }
    else if(game.insufficient_material()) {
        status = "Game over, draw by insufficient material";
        finishGame("insufficient")
    }
    else if(game.in_draw()) {
        status = "Game over, draw by 50-move rule";
        finishGame("50");
    }
    
    if(gameHistory.length != game.history().length) {
        addMoveToHistory();
        $notation.scrollTop($notation.height());
    }

    $status.html(status);
    setTimeout(function() { //has to be in a setTimeout, as board.fen() does not update until method exits
        var fen = game.fen();
        fen = fen.substring(0, fen.indexOf(" "));
        if(fen != board.fen()) {
            board.position(fen, false);
        }
        if(statusHeight != $status.height()) {
            statusHeight = $status.height();
            resizeBoard();
        }
    });
}

function addMoveToHistory() {
    gameHistory = game.history();
    if(gameHistory.length > 0) {
        var move = gameHistory[gameHistory.length - 1];
        if(game.turn() == 'b') {
            $notation.append("<div class=\"notation_row\"><span class=\"notation_number\">" + Math.ceil(gameHistory.length / 2) + "</span><span class=\"white_move hover\" onclick=\"notationClicked(this)\">" + move + "</span><span class=\"black_move\"></span></div>");
        }
        else {
            var blackMove = $($($notation.children().toArray()[Math.floor(gameHistory.length / 2) - 1]).children().toArray()[2]).get()[0];
            blackMove.innerText = move;
            blackMove.className = "black_move hover";
            blackMove.onclick = function(){notationClicked(this)};
        }
    }
}

function onDialogClose() {
    game.move({
        from: promotionStart,
        to: promotionTarget,
        promotion: dialog.returnValue
    });
    updateStatus();
}

function updateFormImages(white) {
    var images = $("button > img").get();
    if(white) {
        images[0].src = wQ;
        images[1].src = wN;
        images[2].src = wR;
        images[3].src = wB;
    }
    else {
        images[0].src = bQ;
        images[1].src = bN;
        images[2].src = bR;
        images[3].src = bB;
    }
}

function queryState() {
    if(!yourTurn && moveUploaded) {
        var queryRequest = new Request("https://thegoodlifetravelguru.com/chess/php/query_game.php", {method: 'POST', body: 'id=' + boardID, headers: {"Content-Type": "application/x-www-form-urlencoded"}});
        fetch(queryRequest)
            .then(response => response.json()
            .then(data => {
                if(data.turn == player && moveUploaded) {
                    goToHistory(gameHistory.length, null);
                    moveUploaded = false;
                    game.move(data.last_move);
                    updateStatus();
                    $notation.scrollTop($notation.height());
                }
            }));
    }
}

var initialIntervalID;
var loaded = false;

function loadCallback() {
    if(!loaded) {
        const loadRequest = new Request("https://thegoodlifetravelguru.com/chess/php/load_game.php", {method: "POST", body: "id=" + boardID, headers: {"Content-Type": "application/x-www-form-urlencoded"}});
        console.log(loadRequest);
        fetch(loadRequest).then(response => {
            response.json().then(data => {
                console.log(data);
                loaded = true;
                white_name = data.white;
                black_name = data.black;
                if(player == "white") {
                    opponent_name = black_name;
                }
                else {
                    opponent_name = white_name;
                }
                $status = $('#status');
                $notation = $('#notation');
                $board_holder = $('#board_holder');
                $sidebar = $('#sidebar');
                $player_indicator = $('#player_indicator');
                $('#white_player').text(white_name);
                $('#black_player').text(black_name);
                dialog = $('#piece_chooser').get()[0];
                dialog.addEventListener('close', onDialogClose);
                if(data.moves) {
                    var moves_seperated = data.moves.split(";");
                    moves_seperated.forEach(move => {
                        game.move(move);
                        addMoveToHistory();
                    });
                }
                
                var config = {
                    draggable: true,
                    position: game.fen(),
                    onDragStart: onDragStart,
                    onDrop: onDrop,
                    pieceTheme: loadPiece,
                    orientation: player
                };
                board = Chessboard('board1', config);
                updateStatus();
                if(!yourTurn) moveUploaded = true;
                updateFormImages(player == "white");
                queryInterval = setInterval(queryState, 1000);
                clearInterval(initialIntervalID);
                setTimeout(function() {$notation.scrollTop($notation.height())});
            });
        });
    }
}

$(document).ready(function() {
    loadCallback();
    initialIntervalID = setInterval(loadCallback, 5000);
});

function loadPiece(piece) {
    switch(piece) {
        case 'wP': return wP;
        case 'wN': return wN;
        case 'wB': return wB;
        case 'wR': return wR;
        case 'wQ': return wQ;
        case 'wK': return wK;
        case 'bP': return bP;
        case 'bN': return bN;
        case 'bB': return bB;
        case 'bR': return bR;
        case 'bQ': return bQ;
        case 'bK': return bK;
    }
    Console.log("invalid piece: " + piece);
    return 'invalid';
}